fetch_files() {
    local FILES=("${!1}")
    local DESTINATION_PATH="$2/$3"

    sudo mkdir -p "$DESTINATION_PATH"

    for FILE in "${FILES[@]}"; do
        FILENAME=$(basename "$FILE")
        sudo curl -J -o "$DESTINATION_PATH/$FILENAME" "$FILE" 2>/dev/null
    done
}

